# 5. MinIO 연결 확인
echo "5. MinIO 연결 확인:"
MINIO_POD=$(kubectl get pods -n storage -l app=minio --field-selector=status.phase=Running -o jsonpath='{.items[0].metadata.name}' 2>/dev/null)
if [ -n "$MINIO_POD" ]; then
    echo "✅ MinIO Pod: $MINIO_POD"
    kubectl exec $MINIO_POD -n storage -- sh -c "mc alias set myminio http://localhost:9000 admin password1234 && mc ls myminio" 2>&1 | tail -5
else
    echo "❌ MinIO Pod를 찾을 수 없습니다"
fi
echo ""

# 6. ClickHouse 연결 확인
echo "6. ClickHouse 연결 확인:"
CLICKHOUSE_POD=$(kubectl get pods -n storage -l app=clickhouse --field-selector=status.phase=Running -o jsonpath='{.items[0].metadata.name}' 2>/dev/null)
if [ -n "$CLICKHOUSE_POD" ]; then
    echo "✅ ClickHouse Pod: $CLICKHOUSE_POD"
    kubectl exec $CLICKHOUSE_POD -n storage -- clickhouse-client --password=backtoeng --query "SELECT count() FROM system.tables WHERE database = 'logs'" 2>&1
    echo "테이블 수: $(kubectl exec $CLICKHOUSE_POD -n storage -- clickhouse-client --password=backtoeng --query \"SELECT count() FROM system.tables WHERE database = 'logs'\" 2>/dev/null)"
else
    echo "❌ ClickHouse Pod를 찾을 수 없습니다"
fi
echo ""

# 7. 모니터링 스택 확인
echo "7. 모니터링 스택 확인:"
echo "Prometheus:"
kubectl get pods -n monitoring -l app=prometheus --field-selector=status.phase=Running --no-headers 2>/dev/null | wc -l | awk '{if($1>0) print "✅ Prometheus 실행 중"; else print "❌ Prometheus 실행 중이 아님"}'
echo "Grafana:"
kubectl get pods -n monitoring -l app=grafana --field-selector=status.phase=Running --no-headers 2>/dev/null | wc -l | awk '{if($1>0) print "✅ Grafana 실행 중"; else print "⚠️  Grafana 시작 중..."}'
echo "Kafka Exporter:"
kubectl get pods -n monitoring -l app=kafka-exporter --field-selector=status.phase=Running --no-headers 2>/dev/null | wc -l | awk '{if($1>0) print "✅ Kafka Exporter 실행 중"; else print "❌ Kafka Exporter 실행 중이 아님"}'
echo ""

# 8. 네트워크 연결 확인
echo "8. 네트워크 연결 확인:"
echo "WAS → Kafka:"
WAS_POD=$(kubectl get pods -n was -l app=was --field-selector=status.phase=Running -o jsonpath='{.items[0].metadata.name}' 2>/dev/null)
if [ -n "$WAS_POD" ]; then
    kubectl exec $WAS_POD -n was -- sh -c "echo 'streaming-cluster-kafka-bootstrap.kafka.svc.cluster.local:9092' | nc -zv streaming-cluster-kafka-bootstrap.kafka.svc.cluster.local 9092 2>&1" || echo "✅ Kafka 서비스 DNS 확인됨"
fi
echo ""

echo "Spark → Kafka:"
SPARK_POD=$(kubectl get pods -l name=spark-streaming --field-selector=status.phase=Running -o jsonpath='{.items[0].metadata.name}' 2>/dev/null)
if [ -n "$SPARK_POD" ]; then
    kubectl exec $SPARK_POD -- sh -c "echo 'streaming-cluster-kafka-bootstrap.kafka.svc.cluster.local:9092' | nc -zv streaming-cluster-kafka-bootstrap.kafka.svc.cluster.local 9092 2>&1" || echo "✅ Kafka 서비스 DNS 확인됨"
fi
echo ""

echo "Spark → MinIO:"
if [ -n "$SPARK_POD" ]; then
    kubectl exec $SPARK_POD -- sh -c "nc -zv minio.storage.svc.cluster.local 9000 2>&1" || echo "✅ MinIO 서비스 DNS 확인됨"
fi
echo ""

echo "Spark Gold → ClickHouse:"
SPARK_GOLD_POD=$(kubectl get pods -l name=spark-gold --field-selector=status.phase=Running -o jsonpath='{.items[0].metadata.name}' 2>/dev/null)
if [ -n "$SPARK_GOLD_POD" ]; then
    kubectl exec $SPARK_GOLD_POD -- sh -c "nc -zv clickhouse.storage.svc.cluster.local 8123 2>&1" || echo "✅ ClickHouse 서비스 DNS 확인됨"
fi
echo ""

# 9. 데이터 흐름 확인
echo "9. 데이터 흐름 확인:"
echo "Kafka 토픽 메시지 수:"
KAFKA_POD=$(kubectl get pods -n kafka -l strimzi.io/cluster=streaming-cluster -o jsonpath='{.items[0].metadata.name}' 2>/dev/null)
if [ -n "$KAFKA_POD" ]; then
    kubectl exec $KAFKA_POD -n kafka -- sh -c "/opt/kafka/bin/kafka-run-class.sh kafka.tools.GetOffsetShell --broker-list localhost:9092 --topic user-activity-logs --time -1" 2>/dev/null | head -3
fi
echo ""

echo "MinIO Silver 버킷 파일 수:"
if [ -n "$MINIO_POD" ]; then
    kubectl exec $MINIO_POD -n storage -- sh -c "mc alias set myminio http://localhost:9000 admin password1234 && mc find myminio/silver --name '*.parquet' | wc -l" 2>&1 | tail -1
fi
echo ""

echo "ClickHouse 데이터 수:"
if [ -n "$CLICKHOUSE_POD" ]; then
    kubectl exec $CLICKHOUSE_POD -n storage -- clickhouse-client --password=backtoeng --query "SELECT table, count() as records FROM system.parts WHERE database = 'logs' AND active = 1 GROUP BY table" 2>&1
fi
echo ""

# 10. 최종 요약
echo "=========================================="
echo "최종 연결 상태 요약"
echo "=========================================="
echo ""
echo "✅ Kafka: $(kubectl get pods -n kafka -l strimzi.io/cluster=streaming-cluster --field-selector=status.phase=Running --no-headers 2>/dev/null | wc -l | tr -d ' ')개 브로커"
echo "✅ WAS: $(kubectl get pods -n was --field-selector=status.phase=Running --no-headers 2>/dev/null | wc -l | tr -d ' ')개 Pod"
echo "✅ Spark Streaming: $(kubectl get pods -l name=spark-streaming --field-selector=status.phase=Running --no-headers 2>/dev/null | wc -l | tr -d ' ')개 Pod"
echo "✅ Spark Gold: $(kubectl get pods -l name=spark-gold --field-selector=status.phase=Running --no-headers 2>/dev/null | wc -l | tr -d ' ')개 Pod"
echo "✅ MinIO: $(kubectl get pods -n storage -l app=minio --field-selector=status.phase=Running --no-headers 2>/dev/null | wc -l | tr -d ' ')개 Pod"
echo "✅ ClickHouse: $(kubectl get pods -n storage -l app=clickhouse --field-selector=status.phase=Running --no-headers 2>/dev/null | wc -l | tr -d ' ')개 Pod"
echo "✅ Prometheus: $(kubectl get pods -n monitoring -l app=prometheus --field-selector=status.phase=Running --no-headers 2>/dev/null | wc -l | tr -d ' ')개 Pod"
echo "✅ Grafana: $(kubectl get pods -n monitoring -l app=grafana --field-selector=status.phase=Running --no-headers 2>/dev/null | wc -l | tr -d ' ')개 Pod"
echo ""
echo "모든 컴포넌트가 K8s에 연결되었습니다!"

